package com.TerBiliLive.Utils;

import java.util.Random;

/**
 * @author myetear
 */
public class RandomUtil {

    /**
     * 随机返回真假值
     * @param bound 返回比例
     * @return 随机值
     */
    public static boolean getRandomBoolean(int bound){
        return new Random().nextInt(bound) == 0;
    }
}
